// Combine KPI data from Calculate Metrics and Process Survey workflows
const allInputData = $input.all();

console.log('=== SAVE KPI WORKFLOW - DATA MERGING ===');
console.log('Received items for KPI merge:', allInputData.length);

// Separate records by type and group by entity
const casePerformanceRecords = new Map();
const satisfactionRecords = new Map();

allInputData.forEach((item, index) => {
  const kpiData = item.json;
  const entityName = kpiData.entity_name || kpiData.entityName;

  // Determine record type
  if (kpiData.metrics?.customerSatisfaction) {
    // This is a satisfaction record from Process Survey
    console.log(`ðŸ“Š Satisfaction record ${index + 1}: ${entityName}`);
    satisfactionRecords.set(entityName, kpiData);
  } else if (kpiData.metrics?.sct !== undefined || kpiData.sample_cases) {
    // This is a case performance record from Calculate Metrics
    console.log(`ðŸ“ˆ Case performance record ${index + 1}: ${entityName}`);
    casePerformanceRecords.set(entityName, kpiData);
  } else {
    console.log(`âš ï¸ Unknown record type ${index + 1}: ${entityName}`);
  }
});

console.log('ðŸ“‹ Data grouping summary:', {
  casePerformanceEntities: Array.from(casePerformanceRecords.keys()),
  satisfactionEntities: Array.from(satisfactionRecords.keys()),
  totalCaseRecords: casePerformanceRecords.size,
  totalSatisfactionRecords: satisfactionRecords.size
});

// Merge data by entity - use case performance as base, add satisfaction data
const mergedRecords = [];

// Process each case performance record as the base
casePerformanceRecords.forEach((caseRecord, entityName) => {
  const satisfactionRecord = satisfactionRecords.get(entityName);

  console.log(`ðŸ”„ Merging data for: ${entityName}`, {
    hasCaseData: !!caseRecord,
    hasSatisfactionData: !!satisfactionRecord
  });

  // Create merged record with case performance as base
  const mergedRecord = {
    entity_id: satisfactionRecord?.entity_id || null, // Use satisfaction entity_id if available
    entity_name: caseRecord.entity_name,
    entity_type: caseRecord.entity_type,
    date: caseRecord.date,

    // Merge metrics: case performance + satisfaction data
    metrics: {
      // Case performance metrics
      sct: caseRecord.metrics?.sct || 0,
      closedCases: caseRecord.metrics?.closedCases || 0,

      // Add satisfaction data if available
      ...(satisfactionRecord?.metrics?.customerSatisfaction && {
        customerSatisfaction: satisfactionRecord.metrics.customerSatisfaction
      }),

      // Add survey details if available
      ...(satisfactionRecord?.metrics?.surveyDetails && {
        surveyDetails: satisfactionRecord.metrics.surveyDetails
      })
    },

    // Case performance specific fields
    cases_count: caseRecord.cases_count || 0,
    sample_cases: caseRecord.sample_cases || [],
    metadata: caseRecord.metadata || {},

    // Timestamps - use most recent or case performance timestamps
    created_at: caseRecord.created_at,
    updated_at: caseRecord.updated_at || new Date(),

    // Source tracking
    source: satisfactionRecord?.source ? 
      `${caseRecord.source || 'case-workflow'} + ${satisfactionRecord.source}` : 
      (caseRecord.source || 'case-workflow')
  };

  mergedRecords.push(mergedRecord);

  console.log(`âœ… Merged record for ${entityName}:`, {
    hasScMetrics: !!(mergedRecord.metrics.sct || mergedRecord.metrics.closedCases),
    hasSatisfactionMetrics: !!mergedRecord.metrics.customerSatisfaction,
    hasSurveyDetails: !!mergedRecord.metrics.surveyDetails,
    casesCount: mergedRecord.cases_count,
    sampleCasesCount: mergedRecord.sample_cases?.length || 0
  });
});

// Handle satisfaction records that don't have corresponding case performance records
satisfactionRecords.forEach((satisfactionRecord, entityName) => {
  if (!casePerformanceRecords.has(entityName)) {
    console.log(`âš ï¸ Satisfaction-only record for: ${entityName} (no case performance data)`);

    // Create record with satisfaction data only
    const satisfactionOnlyRecord = {
      entity_id: satisfactionRecord.entity_id || null,
      entity_name: satisfactionRecord.entity_name,
      entity_type: satisfactionRecord.entity_type,
      date: satisfactionRecord.date,

      metrics: {
        sct: 0, // No case data available
        closedCases: 0, // No case data available
        customerSatisfaction: satisfactionRecord.metrics.customerSatisfaction,
        surveyDetails: satisfactionRecord.metrics.surveyDetails || []
      },

      cases_count: 0,
      sample_cases: [],
      metadata: {
        entityType: satisfactionRecord.entity_type,
        calculationSource: 'satisfaction-only',
        note: 'No case performance data available for this entity'
      },

      created_at: satisfactionRecord.created_at,
      updated_at: new Date(),
      source: satisfactionRecord.source
    };

    mergedRecords.push(satisfactionOnlyRecord);
  }
});

console.log('ðŸŽ¯ Final merged records summary:', {
  totalMergedRecords: mergedRecords.length,
  entitiesWithBothData: casePerformanceRecords.size,
  entitiesWithSatisfactionOnly: satisfactionRecords.size - casePerformanceRecords.size
});

if (mergedRecords.length > 0) {
  console.log('ðŸ“‹ Sample merged record structure:', Object.keys(mergedRecords[0]));
  console.log('ðŸ“Š Sample metrics keys:', Object.keys(mergedRecords[0].metrics));
}

// Return merged records for MongoDB insertion
return mergedRecords.map(record => ({ json: record }));
