// For n8n Code Node - Remove HTML content and PII from API response
const inputData = $input.all();

function cleanHtmlContent(text) {
  if (!text || typeof text !== 'string') return text;

  // Remove HTML tags and decode HTML entities
  return text
    .replace(/<[^>]*>/g, '') // Remove HTML tags
    .replace(/&nbsp;/g, ' ') // Replace non-breaking spaces
    .replace(/&amp;/g, '&') // Replace encoded ampersands
    .replace(/&lt;/g, '<') // Replace encoded less than
    .replace(/&gt;/g, '>') // Replace encoded greater than
    .replace(/&quot;/g, '"') // Replace encoded quotes
    .replace(/&#39;/g, "'") // Replace encoded apostrophes
    .replace(/\s+/g, ' ') // Replace multiple whitespace with single space
    .trim(); // Remove leading/trailing whitespace
}

function removePII(text) {
  if (!text || typeof text !== 'string') return text;

  return text
    // Remove email addresses
    .replace(/\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g, '[EMAIL_REMOVED]')
    // Remove US phone numbers with specific formatting
    .replace(/\b(\+?1[-.\s]?)?\(?([0-9]{3})\)?[-.\s]?([0-9]{3})[-.\s]?([0-9]{4})\b/g, '[PHONE_REMOVED]')
    // Remove international phone numbers with separators
    .replace(/\b\+[1-9]\d{0,3}[-.\s][0-9]{1,4}[-.\s]?[0-9]{1,4}[-.\s]?[0-9]{1,9}\b/g, '[PHONE_REMOVED]')
    // Remove international phone numbers without separators (total 8-15 digits including country code)
    .replace(/\+\d{8,15}\b/g, '[PHONE_REMOVED]')
    // Remove standalone 10-11 digit numbers that are likely phone numbers (not part of timestamps or other data)
    .replace(/\b(?<![0-9T-])\d{10,11}(?![0-9T:-])\b/g, '[PHONE_REMOVED]')
    // Remove system generated email disclaimers (including line breaks)
    .replace(/\*\*\s*This is a system generated email from our case tracking system\*\*\*.*?message\.\s*/gis, '')
    .replace(/This message was sent from outside of Trend Micro\.\s*/gi, '')
    .replace(/Please do not click links or open attachments unless you recognise the source of this email and know the content is safe\.\s*/gi, '')
    .replace(/This email has been received from an external source\. Do not click links or open attachments unless you can confirm the sender and know the content is safe\.\s*/gi, '')
    // Remove additional resources section
    .replace(/Additional Resources.*?Vulnerabilities\s*/gis, '');
}

function processText(text) {
  if (!text || typeof text !== 'string') return text;

  // First clean HTML, then remove PII
  let cleaned = cleanHtmlContent(text);
  cleaned = removePII(cleaned);

  return cleaned;
}

function processObject(obj) {
  if (typeof obj === 'string') {
    return processText(obj);
  } else if (Array.isArray(obj)) {
    return obj.map(function(item) { return processObject(item); });
  } else if (obj && typeof obj === 'object') {
    const cleaned = {};
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) {
        cleaned[key] = processObject(obj[key]);
      }
    }
    return cleaned;
  }
  return obj;
}

// Process all input items
const outputData = inputData.map(function(item) {
  return {
    json: processObject(item.json)
  };
});

return outputData;

function cleanEmailThread(rawContent) {
    if (!rawContent) return '';

    // Step 1: Simple string replacements instead of regex
    let cleaned = rawContent;

    // Remove CSS by finding and removing blocks
    while (cleaned.indexOf('@font-face') !== -1) {
        const start = cleaned.indexOf('@font-face');
        const end = cleaned.indexOf('}', start);
        if (end !== -1) {
            cleaned = cleaned.substring(0, start) + cleaned.substring(end + 1);
        } else {
            break;
        }
    }

    // Remove remaining CSS blocks
    while (cleaned.indexOf('{') !== -1 && cleaned.indexOf('}') !== -1) {
        const start = cleaned.indexOf('{');
        const end = cleaned.indexOf('}', start);
        if (end !== -1) {
            cleaned = cleaned.substring(0, start) + cleaned.substring(end + 1);
        } else {
            break;
        }
    }

    // Simple replacements
    cleaned = cleaned.replace(/&nbsp;/g, ' ');
    cleaned = cleaned.replace(/&amp;/g, '&');
    cleaned = cleaned.replace(/&lt;/g, '<');
    cleaned = cleaned.replace(/&gt;/g, '>');

    // Step 2: Find timestamps manually
    const entries = [];
    const patterns = ['Portal Post', 'Email', 'Case Resolution', 'Support File Upload'];

    for (let i = 0; i < patterns.length; i++) {
        let searchIndex = 0;
        while (true) {
            const patternIndex = cleaned.indexOf(patterns[i], searchIndex);
            if (patternIndex === -1) break;

            // Look for timestamp after pattern
            const timestampStart = patternIndex + patterns[i].length;
            const timestampMatch = cleaned.substring(timestampStart, timestampStart + 50);

            // Simple timestamp check (YYYY-MM-DDTHH:MM:SSZ)
            if (timestampMatch.indexOf('T') !== -1 && timestampMatch.indexOf('Z') !== -1) {
                const spaceIndex = timestampStart + timestampMatch.indexOf(' ');
                const zIndex = timestampStart + timestampMatch.indexOf('Z');

                if (spaceIndex > patternIndex && zIndex > spaceIndex) {
                    const timestamp = cleaned.substring(spaceIndex + 1, zIndex + 1);

                    entries.push({
                        type: patterns[i],
                        timestamp: timestamp,
                        startIndex: patternIndex,
                        endIndex: zIndex + 1
                    });
                }
            }

            searchIndex = patternIndex + 1;
        }
    }

    // Sort entries by position
    entries.sort(function(a, b) {
        return a.startIndex - b.startIndex;
    });

    // Step 3: Extract content between entries
    const emailEntries = [];

    for (let i = 0; i < entries.length; i++) {
        const current = entries[i];
        const next = entries[i + 1];

        const contentStart = current.endIndex;
        const contentEnd = next ? next.startIndex : cleaned.length;
        const content = cleaned.substring(contentStart, contentEnd).trim();

        if (content.length > 10) {
            emailEntries.push({
                type: current.type,
                timestamp: current.timestamp,
                content: lightCleanContent(content),
                title: extractTitle(content)
            });
        }
    }

    // Step 4: Extract case ID
    let caseId = '';
    const tmIndex = cleaned.indexOf('TM-');
    if (tmIndex !== -1) {
        const tmEnd = tmIndex + 3;
        let numEnd = tmEnd;
        while (numEnd < cleaned.length && cleaned.charAt(numEnd) >= '0' && cleaned.charAt(numEnd) <= '9') {
            numEnd++;
        }
        caseId = cleaned.substring(tmIndex, numEnd);
    }

    // Step 5: Format output
    let emailThread = 'EMAIL THREAD - Case ' + caseId + '\n\n';

    for (let i = 0; i < emailEntries.length; i++) {
        const entry = emailEntries[i];
        emailThread += '[' + (i + 1) + '] ' + entry.timestamp + ' | ' + entry.type + '\n';
        emailThread += 'Title: ' + entry.title + '\n';
        emailThread += 'Description: ' + entry.content + '\n';
        emailThread += '----------------------------------------------------------------\n\n';
    }

    return {
        case_id: caseId,
        content: cleaned.replace(/  +/g, ' ').trim(),
        structured_thread: emailThread,
        timestamp: new Date().toISOString()
    };
}

function lightCleanContent(content) {
    if (!content) return '';

    let cleaned = content;

    // Simple string replacements
    cleaned = cleaned.replace(/# CorpIS-D365-CustomerService/g, '');
    cleaned = cleaned.replace(/# Portals-Business Success Portal/g, '');

    // Remove common footers
    if (cleaned.indexOf('Additional Resources') !== -1) {
        cleaned = cleaned.substring(0, cleaned.indexOf('Additional Resources'));
    }

    if (cleaned.indexOf('Copyright') !== -1) {
        cleaned = cleaned.substring(0, cleaned.indexOf('Copyright'));
    }

    // Replace URLs
    let urlStart = cleaned.indexOf('http');
    while (urlStart !== -1) {
        let urlEnd = urlStart;
        while (urlEnd < cleaned.length && cleaned.charAt(urlEnd) !== ' ' && cleaned.charAt(urlEnd) !== '\n') {
            urlEnd++;
        }
        cleaned = cleaned.substring(0, urlStart) + '[Link]' + cleaned.substring(urlEnd);
        urlStart = cleaned.indexOf('http');
    }

    // Clean whitespace
    cleaned = cleaned.replace(/  +/g, ' ').trim();

    return cleaned;
}

function extractTitle(content) {
    if (!content) return 'Email Activity';

    if (content.indexOf('Case Created') !== -1) return 'Case Created';
    if (content.indexOf('Recommend Solutions') !== -1) return 'Recommended Solutions';
    if (content.indexOf('Technical Support Case') !== -1) return 'Technical Support Case Response';
    if (content.indexOf('This is Chris') !== -1) return 'Technical Support Response - CVE Assistance';
    if (content.indexOf('CVE-2024') !== -1) return 'CVE Security Response';
    if (content.indexOf('File Name') !== -1) return 'File Upload';
    if (content.indexOf('PowerBox') !== -1) return 'File Upload';
    if (content.indexOf('RE:') !== -1) return 'Follow-up Email';
    if (content.indexOf('have not heard') !== -1) return 'Follow-up Check-in';
    if (content.indexOf('attention is needed') !== -1) return 'Attention Required';
    if (content.indexOf('awaiting your response') !== -1) return 'Response Required';
    if (content.indexOf('on hold') !== -1) return 'Case On Hold Reminder';
    if (content.indexOf('Closure') !== -1) return 'Case Closure Warning';
    if (content.indexOf('closed due to no response') !== -1) return 'Case Closed - No Response';
    if (content.indexOf('has been closed') !== -1) return 'Case Closed';
    if (content.indexOf('Auto closed') !== -1) return 'Auto Closed by System';

    return 'Email Activity';
}

// Main execution for n8n
const items = $input.all();
const results = [];

for (let i = 0; i < items.length; i++) {
    const item = items[i];
    const emailContent = item.json.content || item.json.description || item.json.body || '';

    if (emailContent) {
        const cleanedEmail = cleanEmailThread(emailContent);

          results.push({
          json: {
          case_id: item.json.case_id,
          priority: item.json.priority,
          structured_email_thread: cleanedEmail.structured_thread,
          owner_full_name: item.json.owner_full_name,
          title: item.json.title,
          products: Array.isArray(item.json.products) ? item.json.products[0] : item.json.products,
          status: item.json.status,
          created_date: item.json.created_date,
          closed_date: item.json.closed_date
              }
      });
    }
}

return results;

function calculateCaseAge(jsonData) {
  return jsonData.map(caseItem => {
    const createdDate = new Date(caseItem.created_date);
    const closedDate = new Date(caseItem.closed_date);
    const caseAgeInDays = Math.ceil((closedDate - createdDate) / (1000 * 60 * 60 * 24));

    return {
      ...caseItem,
      case_age_days: caseAgeInDays
    };
  });
}

// For n8n usage - process the input items
const items = $input.all();
const processedData = items.map(item => item.json);
const result = calculateCaseAge(processedData);

// Return the processed cases with Case Age field
return result.map(caseData => ({ json: caseData }));

