// Get the AI output
const aiResponse = $input.first().json.output;

// Parse the JSON string
let parsedOutput;
try {
  parsedOutput = JSON.parse(aiResponse);
} catch (error) {
  // If parsing fails, return error info
  return [{ json: { error: "Failed to parse JSON", raw_output: aiResponse } }];
}

// Return the parsed JSON
return [{ json: parsedOutput }];
