// Extract entity data using the same logic as Calculate Metrics
// Handle array of DPE objects from trigger
let entityValues = [];
let action = 'processSurvey';
let entityType = 'dpe';
let entityName = 'Unknown';
let totalEntities = 0;

try {
  console.log('=== PROCESS SURVEY - EXTRACT ENTITY DEBUG ===');
  console.log('üöÄ Processing trigger data for DPE array (same as Calculate Metrics)');

  // Try multiple ways to access the trigger data
  let triggerData = null;

  // Method 1: Get first item (might be just one item of array)
  try {
    const method1 = $input.first().json;
    console.log('üìã Method 1 - first().json:', JSON.stringify(method1, null, 2));
    console.log('üìã Method 1 - Type:', typeof method1, 'IsArray:', Array.isArray(method1));
    triggerData = method1;
  } catch (e) {
    console.log('‚ùå Method 1 failed:', e.message);
  }

  // Method 2: Get all items (might be multiple items)
  try {
    const method2 = $input.all();
    console.log('üìã Method 2 - all() length:', method2.length);
    if (method2.length > 0) {
      console.log('üìã Method 2 - all()[0]:', JSON.stringify(method2[0].json, null, 2));
      if (method2.length > 1) {
        console.log('üìã Method 2 - all()[1]:', JSON.stringify(method2[1].json, null, 2));
      }

      // If we have multiple trigger items, combine them into array
      if (method2.length > 1) {
        triggerData = method2.map(item => item.json);
        console.log('‚úÖ Method 2 - Combined multiple trigger items into array');
      } else if (method2.length === 1) {
        triggerData = method2[0].json;
        console.log('‚úÖ Method 2 - Using single trigger item');
      }
    }
  } catch (e) {
    console.log('‚ùå Method 2 failed:', e.message);
  }

  console.log('üìã Final trigger data type:', typeof triggerData);
  console.log('üìã Final trigger data isArray:', Array.isArray(triggerData));
  console.log('üìã Final trigger data:', JSON.stringify(triggerData, null, 2));

  // Handle the array format: [{ entityValue, owner_full_name, ... }, ...]
  if (Array.isArray(triggerData)) {
    console.log('‚úÖ Detected array format with', triggerData.length, 'DPE objects');

    // Extract entity values and create inputData in the same format as Calculate Metrics
    const inputData = triggerData.map((dpeObj, index) => {
      const name = dpeObj.entityValue || dpeObj.owner_full_name;
      console.log(`üìù DPE ${index + 1}: ${name}`);
      return {
        entityValue: name,
        owner_full_name: name,
        entityType: dpeObj.entityType || 'dpe',
        entityName: dpeObj.entityName || name,
        entityIndex: index,
        totalEntities: triggerData.length,
        source: 'process-survey-trigger'
      };
    }).filter(item => item.entityValue);

    // Get metadata from first object (all should have same metadata)
    if (triggerData.length > 0) {
      const firstDpe = triggerData[0];
      action = 'processSurvey';
      entityType = firstDpe.entityType || 'squad';
      entityName = firstDpe.entityName || 'Unknown Squad';
      totalEntities = firstDpe.totalEntities || triggerData.length;
    }

    console.log('üìä Extracted from array:', {
      inputDataLength: inputData.length,
      entityType,
      entityName,
      totalEntities,
      action
    });

    return [{ 
      json: { 
        inputData: inputData,
        processedCount: 0,
        totalInputs: inputData.length,
        timestamp: new Date().toISOString(),
        source: 'process-survey-extract-entity',
        entityType: entityType,
        entityName: entityName,
        action: action
      } 
    }];
  }
  // Handle single object format (fallback)
  else if (triggerData && typeof triggerData === 'object') {
    console.log('‚úÖ Detected single object format');

    action = triggerData.action || 'processSurvey';
    entityType = triggerData.entityType || 'dpe';
    entityName = triggerData.entityName || triggerData.entityValue || 'Unknown';

    const inputData = [{
      entityValue: triggerData.entityValue || triggerData.owner_full_name,
      owner_full_name: triggerData.entityValue || triggerData.owner_full_name,
      entityType: entityType,
      entityName: entityName,
      entityIndex: 0,
      totalEntities: 1,
      source: 'process-survey-single-entity'
    }];

    return [{ 
      json: { 
        inputData: inputData,
        processedCount: 0,
        totalInputs: 1,
        timestamp: new Date().toISOString(),
        source: 'process-survey-single-entity',
        entityType: entityType,
        entityName: entityName,
        action: action
      } 
    }];
  }

} catch (error) {
  console.error('‚ùå ERROR in Process Survey Extract Entity:', error.message);
  console.error('Stack trace:', error.stack);

  // Return default fallback
  const fallbackData = [{
    entityValue: 'Fallback Entity',
    owner_full_name: 'Fallback Entity',
    entityType: 'dpe',
    entityName: 'Fallback Entity',
    entityIndex: 0,
    totalEntities: 1,
    source: 'process-survey-error-fallback'
  }];

  return [{ 
    json: { 
      inputData: fallbackData,
      processedCount: 0,
      totalInputs: 1,
      timestamp: new Date().toISOString(),
      source: 'process-survey-error-fallback',
      error: error.message
    } 
  }];
}

