// Transform raw Google Sheets survey data for MongoDB storage
// Filter to only include the specified columns
// Process ALL input items, not just the first one
const allInputItems = $input.all();

console.log('=== TRANSFORM RAW SURVEY DATA ===');
console.log('Number of input items:', allInputItems.length);

// Collect all survey data from all input items
let allSurveyData = [];
allInputItems.forEach((item, index) => {
  const rawSurveyData = item.json;
  console.log(`Processing input item ${index}:`, typeof rawSurveyData, Array.isArray(rawSurveyData));

  // Handle different survey data formats
  let surveyData = [];
  if (Array.isArray(rawSurveyData)) {
    surveyData = rawSurveyData;
  } else if (rawSurveyData && rawSurveyData.values) {
    surveyData = Array.isArray(rawSurveyData.values) ? rawSurveyData.values : [];
  } else if (rawSurveyData && typeof rawSurveyData === 'object') {
    surveyData = [rawSurveyData];
  } else {
    surveyData = [];
  }

  allSurveyData = allSurveyData.concat(surveyData);
});

console.log('Total survey records collected:', allSurveyData.length);

// Define the columns to keep
const columnsToKeep = [
  'Owner (Case) (Case)',
  'Case Number (Case) (Case)', 
  'Product Name (Case) (Case)',
  'Technical Ability of Your Engineer',
  'Overall Satisfaction',
  'Overall Product Satisfaction',
  'Additional Feedback or Commentary(Text)',
  'Priority (Case) (Case)',
  'Created On'
];

// Filter each survey record to only include specified columns
const filteredSurveys = allSurveyData.map((survey, index) => {
  const filteredSurvey = {
    _id: new Date().getTime() + Math.random() + index, // Unique ID with index
    timestamp: new Date().toISOString(),
    source: 'google-sheets-raw-import'
  };

  // Add only the specified columns
  columnsToKeep.forEach(column => {
    if (survey.hasOwnProperty(column)) {
      filteredSurvey[column] = survey[column];
    }
  });

  return filteredSurvey;
});

console.log('Filtered', filteredSurveys.length, 'records for raw storage');
if (filteredSurveys.length > 0) {
  console.log('Sample filtered record:', JSON.stringify(filteredSurveys[0], null, 2));
  console.log('Last filtered record:', JSON.stringify(filteredSurveys[filteredSurveys.length - 1], null, 2));
}

// Return ALL filtered surveys for MongoDB insertion
return filteredSurveys.map(survey => ({ json: survey }));

// Save satisfaction data to performance_data database in metrics field
// This enables Customer Survey Distribution to display CSAT/Neutral/DSAT by entity
const allSatisfactionData = $input.all();

console.log('=== SAVE SATISFACTION TO PERFORMANCE_DATA ===');
console.log('Received satisfaction items:', allSatisfactionData.length);

const performanceUpdates = [];
const currentDate = new Date().toISOString().split('T')[0]; // YYYY-MM-DD format
const currentTimestamp = new Date().toISOString();

for (const item of allSatisfactionData) {
  const satisfactionData = item.json;

  console.log(`Processing satisfaction data for: ${satisfactionData.entityName}`);
  console.log(`Entity type: ${satisfactionData.entityType}`);
  console.log(`Satisfaction:`, satisfactionData.satisfaction);

  // Create unique identifier with timestamp to prevent duplicates
  const uniqueId = `satisfaction-${satisfactionData.entityType}-${satisfactionData.entityName}-${Date.now()}`.replace(/[^a-zA-Z0-9-]/g, '-').toLowerCase();

  // Prepare performance data record matching the expected structure
  const performanceUpdate = {
    entity_id: satisfactionData.entityId || uniqueId,
    entity_name: satisfactionData.entityName,
    entity_type: satisfactionData.entityType,
    date: currentDate,

    // üöÄ UPDATED METRICS: Clean structure without SCT/cases/satisfaction placeholders (v2.1)
    metrics: {
      // Detailed customer satisfaction breakdown
      customerSatisfaction: {
        csat: satisfactionData.satisfaction.csat || 0,
        neutral: satisfactionData.satisfaction.neutral || 0,
        dsat: satisfactionData.satisfaction.dsat || 0,
        total: satisfactionData.satisfaction.total || 0,
        csatPercentage: satisfactionData.satisfaction.csatPercentage || 0,
        neutralPercentage: satisfactionData.satisfaction.neutralPercentage || 0,
        dsatPercentage: satisfactionData.satisfaction.dsatPercentage || 0,
        lastUpdated: currentTimestamp,
        source: 'satisfaction-survey-workflow'
      }
    },

    // Include survey details for drill-down capability - moved to root level
    surveyDetails: satisfactionData.surveyDetails || [],

    created_at: new Date(),
    source: 'satisfaction-workflow'
  };

  console.log(`Performance update for ${satisfactionData.entityName}:`, {
    uniqueId: uniqueId,
    entityName: performanceUpdate.entity_name,
    hasMetrics: !!performanceUpdate.metrics,
    hasSatisfaction: !!performanceUpdate.metrics.customerSatisfaction,
    csatPercentage: performanceUpdate.metrics.customerSatisfaction.csatPercentage,
    totalSurveys: performanceUpdate.metrics.customerSatisfaction.total,
    surveyCount: performanceUpdate.surveyDetails.length
  });

  performanceUpdates.push(performanceUpdate);
}

console.log(`Prepared ${performanceUpdates.length} performance updates with unique IDs`);
console.log('Sample performance update:', JSON.stringify(performanceUpdates[0] || {}, null, 2));

// Debug: Log the final structure that will be sent to MongoDB
performanceUpdates.forEach((update, index) => {
  console.log(`UPDATE ${index}:`, {
    hasEntityName: !!update.entity_name,
    hasEntityType: !!update.entity_type,
    hasMetrics: !!update.metrics,
    hasCustomerSatisfaction: !!update.metrics?.customerSatisfaction,
    keys: Object.keys(update)
  });
});

// Return the performance updates for MongoDB insertMany operation
// For insertMany, each item should be a complete document
return performanceUpdates.map(update => ({ json: update }));

