const results = [];

// Process each entity from the input (can be DPE, Squad, or Team)
for (const entity of inputData) {
  const entityType = entity.entityType || 'dpe';
  const entityName = entity.entityName || entity.owner_full_name;
  const entityValue = entity.entityValue || entity.owner_full_name;

  let relevantSurveys = [];

  if (allSurveyData.length === 0) {
    console.log(`‚ö†Ô∏è No survey data available for entity: ${entityName}`);
  } else {
    console.log(`üîç Searching surveys for entity: ${entityName} (type: ${entityType})`);
    console.log(`   entityValue: ${entityValue}`);
    console.log(`   owner_full_name: ${entity.owner_full_name}`);

    // Add null/undefined checks before toLowerCase() calls
    if (entityType && entityType.toLowerCase() === 'dpe') {
      // For DPE: Find surveys for this specific person
      const ownerName = entity.owner_full_name || entityValue;
      console.log(`   Searching for DPE owner: "${ownerName}"`);      
      relevantSurveys = allSurveyData.filter(survey => {
        // Fixed field priority - check (Case) (Case) first
        const surveyOwner = survey['Owner (Case) (Case)'] || survey['Owner (Case)'] || survey['Owner'] || survey['Case Owner'];

        // SIMPLIFIED EXACT MATCH ONLY APPROACH
        let matches = false;
        if (surveyOwner && ownerName) {
          const surveyOwnerStr = surveyOwner.toString().toLowerCase().trim();
          const ownerNameStr = ownerName.toString().toLowerCase().trim();

          console.log(`   üîç EXACT MATCHING ONLY: "${surveyOwnerStr}" vs "${ownerNameStr}"`);

          // Strategy 1: Exact match ONLY - No other strategies
          if (surveyOwnerStr === ownerNameStr) {
            matches = true;
            console.log(`   ‚úÖ EXACT FULL NAME MATCH found`);
          }
          // All other matching strategies removed as requested
        }

        if (surveyOwner) {
          console.log(`   Survey owner "${surveyOwner}" vs "${ownerName}" = ${matches}`);
        }

        return matches;
      });
    } else if (entityType && (entityType.toLowerCase() === 'squad' || entityType.toLowerCase() === 'team')) {
      // For Squad/Team: Find surveys for this individual member only (no squad name matching)
      const ownerName = entity.owner_full_name || entityValue;
      console.log(`   Searching for ${entityType} member: "${ownerName}"`);      
      relevantSurveys = allSurveyData.filter(survey => {
        // Fixed field priority - check (Case) (Case) first
        const surveyOwner = survey['Owner (Case) (Case)'] || survey['Owner (Case)'] || survey['Owner'] || survey['Case Owner'];

        // SIMPLIFIED EXACT MATCH ONLY APPROACH
        let memberMatch = false;
        if (surveyOwner && ownerName) {
          const surveyOwnerStr = surveyOwner.toString().toLowerCase().trim();
          const ownerNameStr = ownerName.toString().toLowerCase().trim();

          console.log(`   üîç EXACT MATCHING ONLY: "${surveyOwnerStr}" vs "${ownerNameStr}"`);

          // Strategy 1: Exact match ONLY - No other strategies
          if (surveyOwnerStr === ownerNameStr) {
            memberMatch = true;
            console.log(`   ‚úÖ EXACT FULL NAME MATCH found`);
          }
          // All other matching strategies removed as requested
        }

        if (surveyOwner) {
          console.log(`   Survey owner "${surveyOwner}" vs member "${ownerName}" = ${memberMatch}`);
        }

        return memberMatch;
      });
    }
  }

  console.log(`üìä Found ${relevantSurveys.length} relevant surveys for ${entityName}`);

  // Enhanced debugging for survey matching
  if (relevantSurveys.length === 0) {
    console.log(`‚ùå NO SURVEYS FOUND for ${entityName}. Debugging:`);
    console.log(`   - Entity name: "${entityName}"`);
    console.log(`   - Entity value: "${entityValue}"`);
    console.log(`   - owner_full_name: "${entity.owner_full_name}"`);
    console.log(`   - Total surveys available: ${allSurveyData.length}`);

    // Show sample of available survey owners for comparison
    const availableOwners = allSurveyData.slice(0, 5).map(s => {
      return s['Owner (Case) (Case)'] || s['Owner (Case)'] || s['Owner'] || s['Case Owner'] || 'NO_OWNER';
    });
    console.log(`   - Sample survey owners: [${availableOwners.join(', ')}]`);
  }

  if (relevantSurveys.length > 0) {
    console.log('Survey details:');
    relevantSurveys.forEach((survey, idx) => {
      const satisfaction = survey['Overall satisfaction'] || survey['Overall_satisfaction'] || survey['Technical Ability of Your Engineer'];
      const caseNum = survey['Case Number'] || survey['Case_Number'] || survey['Case ID'] || survey['Case Number (Case) (Case)'];
      console.log(`   ${idx + 1}: Case=${caseNum}, Satisfaction=${satisfaction}`);
    });
  }

  let csatCount = 0;
  let neutralCount = 0;
  let dsatCount = 0;
  const surveyDetails = [];

  console.log(`üîÑ Processing ${relevantSurveys.length} surveys for satisfaction calculation...`);

  // Process each relevant survey response
  for (const survey of relevantSurveys) {
    console.log(`Processing survey:`, JSON.stringify(survey, null, 2).substring(0, 300));

