      category = 'csat';
      console.log(`   ‚úÖ CSAT (${overallSatisfaction} >= 4) - csatCount now: ${csatCount}`);
    } else if (overallSatisfaction === 3) {
      neutralCount++;
      category = 'neutral';
      console.log(`   üòê NEUTRAL (${overallSatisfaction} = 3) - neutralCount now: ${neutralCount}`);
    } else if (overallSatisfaction <= 2 && overallSatisfaction > 0) {
      dsatCount++;
      category = 'dsat';
      console.log(`   ‚ùå DSAT (${overallSatisfaction} <= 2) - dsatCount now: ${dsatCount}`);
    } else {
      console.log(`   ‚ö†Ô∏è INVALID/ZERO satisfaction (${overallSatisfaction}) - not counted`);
    }

    // Store survey details
    surveyDetails.push({
      caseNumber: caseNumber,
      overallSatisfaction: overallSatisfaction,
      category: category,
      feedback: feedback,
      surveyDate: survey['Created On'] || survey['Date'] || survey['Survey Date'] || new Date().toISOString(),
      customerName: survey['Customer Name'] || survey['Customer'] || '',
      productArea: survey['Product Area'] || survey['Product'] || '',
      ownerName: surveyOwner
    });
  }

  console.log(`üìä Final counts: CSAT=${csatCount}, Neutral=${neutralCount}, DSAT=${dsatCount}`);

  // Calculate percentages
  const totalSurveys = csatCount + neutralCount + dsatCount;
  const csatPercentage = totalSurveys > 0 ? Math.round((csatCount / totalSurveys) * 100) : 0;
  const neutralPercentage = totalSurveys > 0 ? Math.round((neutralCount / totalSurveys) * 100) : 0;
  const dsatPercentage = totalSurveys > 0 ? Math.round((dsatCount / totalSurveys) * 100) : 0;

  // Create satisfaction object with validation
  const satisfactionData = {
    csat: csatCount || 0,
    neutral: neutralCount || 0,
    dsat: dsatCount || 0,
    total: totalSurveys || 0,
    csatPercentage: csatPercentage || 0,
    neutralPercentage: neutralPercentage || 0,
    dsatPercentage: dsatPercentage || 0
  };

  // Log satisfaction data for this entity
  console.log(`Entity ${entityName} satisfaction:`, JSON.stringify(satisfactionData, null, 2));

  // Prepare result object with dynamic entity information
  const resultObject = {
    entityId: entityValue,
    entityType: entityType,
    entityName: entityName,
    owner_full_name: entity.owner_full_name || entityValue,
    entityIndex: entity.entityIndex || 0,
    totalEntities: entity.totalEntities || 1,
    satisfaction: satisfactionData,
    surveyDetails: surveyDetails,
    timestamp: new Date().toISOString(),
    source: 'satisfaction-survey-workflow'
  };

  // Validate the result object
  console.log(`Result object for ${entityName}:`, {
    hasEntityId: !!resultObject.entityId,
    hasEntityType: !!resultObject.entityType,
    hasEntityName: !!resultObject.entityName,
    hasSatisfaction: !!resultObject.satisfaction,
    satisfactionKeys: Object.keys(resultObject.satisfaction || {}),
    satisfactionValues: Object.values(resultObject.satisfaction || {})
  });

  results.push(resultObject);
}

console.log(`‚úÖ Processed ${results.length} entities with satisfaction data`);

// Final validation of all results
results.forEach((result, index) => {
  console.log(`Final result ${index}:`, {
    entityName: result.entityName,
    hasSatisfaction: !!result.satisfaction,
    satisfactionStructure: result.satisfaction,
    surveyCount: result.surveyDetails ? result.surveyDetails.length : 0
  });
});

// Ensure each result has proper structure
const validatedResults = results.map((result, index) => {
  if (!result.satisfaction) {
    console.warn(`‚ö†Ô∏è Result ${index} missing satisfaction data, adding defaults`);
    result.satisfaction = {
      csat: 0,
      neutral: 0,
      dsat: 0,
      total: 0,
      csatPercentage: 0,
      neutralPercentage: 0,
      dsatPercentage: 0
    };
  }
  return { json: result };
});

console.log('üîÑ Returning validated results:', validatedResults.length);
return validatedResults;
